SCRIPT.MSCRI:
:INCLUDE PCK.AUTOMIC_JBOSS_V6.PRV.INCLUDE.ATTACH :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHANGE_DIRECTORY_TO_AGENT_BIN :INCLUDE PCK.AUTOMIC_JBOSS_V6.PRV.INCLUDE.JBOSS_CLI@UNIX
:SET &UC4RB_JBOSS_CLI# = &UC4_JBOSS_CLI#
:IF &UC4RB_JBOSS_OPERATING_MODE# = 'Managed Domain' : IF &UC4RB_JBOSS_PROFILE# = '' : PRINT Profile must not be empty : EXIT 1 : ELSE : SET &UC4RB_JBOSS_DS_QUERY_CMD# = '/profile=&UC4RB_JBOSS_PROFILE#/subsystem=datasources' : SET &UC4RB_JBOSS_DS_COMMAND# = 'data-source --profile=&UC4RB_JBOSS_PROFILE#' : ENDIF :ELSE : SET &UC4RB_JBOSS_DS_QUERY_CMD# = '/subsystem=datasources' : SET &UC4RB_JBOSS_DS_COMMAND# = 'data-source' :ENDIF
trim() { local var=$@ var="${var#"${var%%[![:space:]]*}"}" var="${var%"${var##*[![:space:]]}"}" echo -n "$var" }
jndi_name='' driver_name='' connection_url='' driver_class='' user_name='' password='' max_pool_size='' min_pool_size='' enabled='' jta='' connection_properties=false idx=0
! Read content of report file while read line do ! process connection properties if $connection_properties; then line=$(trim $line) if [[ ! "$line" == value=* ]] ; then connection_props_keys[$idx]=$line else val=${line#value=} connection_props_values[$idx]=$val idx=$((idx+1)) fi continue fi
idx=$(echo `expr index "${line}" '='`) if [ $idx -gt 0 ] ; then key=$(echo ${line:0:$idx-1}) val=$(echo ${line:$idx})
if [ "${key}" = 'jndi-name' ]; then jndi_name="${val}" fi; if [ "${key}" = 'driver-name' ]; then driver_name="${val}" fi; if [ "${key}" = 'connection-url' ]; then connection_url="${val}" fi; if [ "${key}" = 'driver-class' ]; then driver_class="${val}" fi; if [ "${key}" = 'user-name' ]; then user_name="${val}" fi; if [ "${key}" = 'password' ]; then enc_passwd="${val}" decryption=$(java -jar "&$AGENT_RESOURCES_CLIENT#jboss-v6-tool.jar" ARB -cmd cipher decrypt "$enc_passwd" | grep "^Decrypted: .*") password=$(echo ${decryption:11}) fi; if [ "${key}" = 'max-pool-size' ]; then max_pool_size="${val}" fi; if [ "${key}" = 'min-pool-size' ]; then min_pool_size="${val}" fi; if [ "${key}" = 'enabled' ]; then enabled="${val}" fi; if [ "${key}" = 'jta' ]; then jta="${val}" fi; else line=$(trim $line) if [ "$line" = "connection-properties" ]; then connection_properties=true fi fi; done < &UC4RB_BACKUP_REPORT#
! Prepare datasource params uc4_ds_params='' if [ "$jndi_name" = 'n/a' ] || [ "$jndi_name" = '' ]; then uc4_ds_params=$(printf "%s--jndi-name=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--jndi-name=%s " "$uc4_ds_params" "$jndi_name") fi;
if [ "$driver_name" = 'n/a' ] || [ "$driver_name" = '' ]; then uc4_ds_params=$(printf "%s--driver-name=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--driver-name=%s " "$uc4_ds_params" "$driver_name") fi;
if [ "$connection_url" = 'n/a' ] || [ "$connection_url" = '' ]; then uc4_ds_params=$(printf "%s--connection-url=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--connection-url=%s " "$uc4_ds_params" "$connection_url") fi;
if [ "$driver_class" = 'n/a' ] || [ "$driver_class" = '' ]; then uc4_ds_params=$(printf "%s--driver-class=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--driver-class=%s " "$uc4_ds_params" "$driver_class") fi;
if [ "$user_name" = 'n/a' ] || [ "$user_name" = '' ]; then uc4_ds_params=$(printf "%s--user-name=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--user-name=%s " "$uc4_ds_params" "$user_name") fi;
if [ "$password" = 'n/a' ] || [ "$password" = '' ]; then uc4_ds_params=$(printf "%s--password=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--password=%s " "$uc4_ds_params" "$password") fi;
if [ "$max_pool_size" = 'n/a' ] || [ "$max_pool_size" = '' ]; then uc4_ds_params=$(printf "%s--max-pool-size=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--max-pool-size=%s " "$uc4_ds_params" "$max_pool_size") fi;
if [ "$min_pool_size" = 'n/a' ] || [ "$min_pool_size" = '' ]; then uc4_ds_params=$(printf "%s--min-pool-size=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--min-pool-size=%s " "$uc4_ds_params" "$min_pool_size") fi;
if [ "$jta" = 'n/a' ] || [ "$jta" = '' ]; then uc4_ds_params=$(printf "%s--jta=undefined " "$uc4_ds_params") else uc4_ds_params=$(printf "%s--jta=%s " "$uc4_ds_params" "$jta") fi;
echo Rollback datasource &UC4RB_JBOSS_NAME# ..
&UC4RB_JBOSS_CLI# --command="&UC4RB_JBOSS_DS_COMMAND# --name=&UC4RB_JBOSS_NAME# $uc4_ds_params"
! Reload required result=$(&UC4RB_JBOSS_CLI# --command=":reload")
! update connection properties if $connection_properties; then for i in ${!connection_props_keys[*]} do name=${connection_props_keys[$i]} value=${connection_props_values[$i]} command="&UC4RB_JBOSS_DS_QUERY_CMD#/data-source=&UC4RB_JBOSS_NAME#/connection-properties=$name/:add(value=\"$value\")" result=$(&UC4RB_JBOSS_CLI# --command="$command") done fi
echo Successfully rollback datasource &UC4RB_JBOSS_NAME#. :INCLUDE PCK.ITPA_SHARED.PRV.INCLUDE.CHECK_SHELL_CMD@UNIX
